import "./App.css";
import background from "./bg.jpg"; // import your image

function App() {
  return (
    <div
      className="app"
      style={{
        backgroundImage: `url(${background})`,
      }}
    >
      {/* Header */}
      <div className="header">
        <div className="left">
          <a href="/">Home</a>
          <a href="/about">About</a>
        </div>
        <div className="right">
          <button className="upload-btn">Upload</button>
        </div>
      </div>

      {/* Content */}
      <div className="content">
        <h1>Welcome to the Capstone Project</h1>
        <p>
          This platform is designed to help visualize and manage mining
          operations efficiently. You can upload data, generate reports, and
          track progress all in one place.
        </p>
      </div>

      {/* Footer */}
      <div className="footer">
        Â© Group15 Capstone Project | All rights reserved
      </div>
    </div>
  );
}

export default App;
