import React, { useState } from "react";
import "./App.css";

function App() {
  const [showUpload, setShowUpload] = useState(false);

  return (
    <div className="app">
      {/* Header */}
      <header className="header">
        <div className="left">
          <a href="#intro">Introduction</a>
          <a href="#about">About</a>
        </div>
        <div className="right">
          <button onClick={() => setShowUpload(!showUpload)}>
            {showUpload ? "Close Upload" : "Upload"}
          </button>
          {showUpload && (
            <input type="file" className="upload-input" />
          )}
        </div>
      </header>

      {/* Sidebar */}
      <aside className="sidebar">
        <h3>Sidebar</h3>
        <ul>
          <li>Menu 1</li>
          <li>Menu 2</li>
          <li>Menu 3</li>
        </ul>
      </aside>

      {/* Main Content */}
      <main className="content">
        <h1>Welcome to Mining Dashboard</h1>
        <p>This is your main project content area.</p>
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>Capstone Project Â© 2025</p>
      </footer>
    </div>
  );
}

export default App;
