import React, { useState, useRef } from "react";  // ✅ Added useRef
import "./App.css";
import background from "./background.jpg"; // import your image

function App() {
  const [activePage, setActivePage] = useState("home");
  const [uploadedFile, setUploadedFile] = useState(null);
  const fileInputRef = useRef(null); // ✅ fixed useRef

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFile(file.name);
      console.log("Uploaded file:", file);
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current.click(); // programmatically click the hidden input
  };

  return (
    <div
      className="app"
      style={{
        backgroundImage: `url(${background})`,
      }}
    >
      {/* Header */}
      <div className="header">
        <div className="left">
          <button
            className={activePage === "home" ? "active" : ""}
            onClick={() => setActivePage("home")}
          >
            Home
          </button>
          <button
            className={activePage === "about" ? "active" : ""}
            onClick={() => setActivePage("about")}
          >
            About
          </button>
        </div>
        <div className="right">
          <button className="upload-btn" onClick={handleUploadClick}>
            Upload
          </button>
          <input
            type="file"
            ref={fileInputRef}
            style={{ display: "none" }}
            onChange={handleFileChange}
          />
        </div>
      </div>

      {/* Content with fade effect */}
      <div className={`content ${activePage}`}>
        {activePage === "home" && (
          <>
            <h1>WELCOME</h1>
            <p>
              Mineral exploration in Western Australia is a critical activity that
              supports the greater mining industry by discovering and defining
              critical resource deposits. The earliest phase of exploration seeks
              to identify regions which likely contain precious minerals at
              economic scale. Typical indicators include: High-grade drillhole
              intercepts which are open along strike or at depth; The presence of
              ‘partner minerals’ which correlate with the presence of the mineral
              of interest; Economic-level interpolated or imputed values which are
              yet untested by real sampling. Early explorations are further
              supported and enhanced by government-hosted public datasets of
              exploration drillhole records.
            </p>
          </>
        )}

        {activePage === "about" && (
          <>
            <h2>About The Project</h2>
            <p>
              This project focuses on Tellurium (Te) assay values within a subset of this publicly hosted data.
              Tellurium is a critical mineral/metal of high significance globally and recently identified as a key
              pathfinder element for gold exploration in WA. A recent study generated a derived dataset
              containing imputed values for samples where Te was not originally analysed. The imputed values
              introduce new information that could guide exploration decisions, particularly in identifying
              prospective zones where Te may have been missed during the initial sampling phase.
              <br /><br />
              The primary objective of the project is to provide a platform which allows an explorer to easily
              compare the original laboratory assay results with the imputed values and quickly identify areas
              with significant deviation between the datasets which may indicate prospective but unexplored
              regions. Within a typical environment, at full scale of the data, differences between the original and
              imputed datasets are not easily visualised or interpreted using traditional methods. This project will
              trial data analysis and visualisation techniques to detect patterns and highlight zones of divergence
              between datasets. A combination of exploratory data analysis, geostatistical comparisons, and
              visualisation tools will aim to reveal “hidden” exploration targets that would otherwise be
              overlooked by the human eye.
              <br /><br />
              While this proof-of-concept will focus on Te, the broader aim is to design a flexible analytical
              platform that could extend to other elements. Users would be able to input their own paired
              datasets (original and imputed) for different elements, allowing for scalable and element-agnostic
              geoscientific analysis. The outcome will support smarter targeting decisions in mineral exploration,
              combining machine-aided inference with interactive visual interpretation.
            </p>
          </>
        )}
      </div>

      {/* Footer */}
      <div className="footer">
        © Group15 Capstone Project | All rights reserved
      </div>
    </div>
  );
}

export default App;
