import React, { useState } from "react";

function DataSelection() {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);

  // Reset everything
  const handleReset = () => {
    setFile1(null);
    setFile2(null);
  };

  // Run comparison (backend placeholder)
  const runComparison = async () => {
    try {
      if (!file1 || !file2) return;
      const form = new FormData();
      form.append("files", file1);
      form.append("files", file2);
      const res = await fetch("http://127.0.0.1:5000/run-comparison", {
        method: "POST",
        body: form,
      });
      const data = await res.json();
      console.log("Run Comparison response:", data);
    } catch (e) {
      console.error("Run Comparison failed:", e);
    }
  };

  // Export (backend placeholder)
  const exportCellPoints = () => {
    window.location.href = "http://127.0.0.1:5000/export/comp-grid.csv";
  };

  return (
    <div>
      <h2>Welcome, upload your files to get started</h2>
      <p><em>Please upload 2 files:</em></p>

      <div style={{ marginBottom: "10px" }}>
        <label>
          Original File:{" "}
          <input
            type="file"
            accept=".parquet,.geoparquet,.zip"
            onChange={(e) => setFile1(e.target.files[0])}
          />
          {file1 && <span> ✅</span>}
        </label>
      </div>

      <div style={{ marginBottom: "10px" }}>
        <label>
          Imputed (DL) File:{" "}
          <input
            type="file"
            accept=".parquet,.geoparquet,.zip"
            onChange={(e) => setFile2(e.target.files[0])}
          />
          {file2 && <span> ✅</span>}
        </label>
      </div>

      <div style={{ marginTop: "15px" }}>
        <button disabled={!file1 || !file2} onClick={runComparison}>
          Run Comparison
        </button>
        <button disabled={!file1 || !file2} onClick={exportCellPoints}>
          Export Cell Points
        </button>
        <button onClick={handleReset}>Reset</button>
      </div>
    </div>
  );
}

export default DataSelection;
