import React, { useState } from "react";
import "./App.css";
import background from "./bg.jpg";

function App() {
  const [activePage, setActivePage] = useState("home");
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);

  // Reset everything
  const handleReset = () => {
    setFile1(null);
    setFile2(null);
  };

  return (
    <div
      className="app"
      style={{
        backgroundImage: `url(${background})`,
      }}
    >
      {/* Header */}
      <div className="header">
        <div className="left">
          <button
            className={activePage === "home" ? "active" : ""}
            onClick={() => setActivePage("home")}
          >
            Home
          </button>
          <button
            className={activePage === "about" ? "active" : ""}
            onClick={() => setActivePage("about")}
          >
            About
          </button>
          <button
            className={activePage === "upload" ? "active" : ""}
            onClick={() => setActivePage("upload")}
          >
            Upload
          </button>
        </div>
      </div>

      {/* Content */}
      <div className={`content ${activePage}`}>
        {activePage === "home" && (
          <>
            <h1>WELCOME</h1>
            <p>
              Mineral exploration in Western Australia is a critical activity
              that supports the greater mining industry by discovering and
              defining critical resource deposits. The earliest phase of
              exploration seeks to identify regions which likely contain precious
              minerals at economic scale.
            </p>
          </>
        )}

        {activePage === "about" && (
          <>
            <h2>About The Project</h2>
            <p>
              This project focuses on Tellurium (Te) assay values within a subset
              of publicly hosted exploration data. Tellurium is a critical
              pathfinder element for gold exploration in WA. A derived dataset
              with imputed values was created where Te was not originally
              analysed. The imputed values may highlight prospective but
              overlooked zones, improving exploration decisions.
            </p>
          </>
        )}

        {activePage === "upload" && (
          <>
            <h2>Upload Your Files</h2>
            <p>Please upload <strong>2 files</strong> for comparison:</p>

            <div>
              <label>
                Choose File 1:
                <input
                  type="file"
                  onChange={(e) => setFile1(e.target.files[0])}
                />
                {file1 && <span> ✅</span>}
              </label>
            </div>

            <div>
              <label>
                Choose File 2:
                <input
                  type="file"
                  onChange={(e) => setFile2(e.target.files[0])}
                />
                {file2 && <span> ✅</span>}
              </label>
            </div>

            <div style={{ marginTop: "15px" }}>
              <button disabled={!file1 || !file2}>Run Comparison</button>
              <button disabled={!file1 || !file2}>Export Cell Points</button>
              <button onClick={handleReset}>Reset</button>
            </div>
          </>
        )}
      </div>

      {/* Footer */}
      <div className="footer">
        © Group15 Capstone Project | All rights reserved
      </div>
    </div>
  );
}

export default App;
